con:config.script:
//Register your JDBC driver
com.eviware.soapui.support.GroovyUtils.registerJdbcDriver("com.mysql.jdbc.Driver")

//import necessary package
import groovy.sql.Sql

//Mention the DB connection string information
//Define your database connection parameters
def dbpath = 'jdbc:mysql://localhost/sqltest'
def dbuser = 'root'
def dbpwd = 'password'
def dbdriver = 'com.mysql.jdbc.Driver'

//establish connection by creating a connection object 
def con = Sql.newInstance(dbpath, dbuser, dbpwd, dbdriver)
log.info 'The connection is' + con

//Specify the query that you want to run
def result = con.firstRow('select * from employee')
log.info 'The record in database is' +result

//Script to do read property value from response and transfer it

def groovyUtils = new com.eviware.soapui.support.GroovyUtils(context)
//creating a holder to hold the response
holder= groovyUtils.getXmlHolder("Test Request#Response")
log.info holder
//read the node values from response
def rate = holder.getNodeValue("//ns1:ConversionRateResponse[1]/ns1:ConversionRateResult[1]")

//customized assertions 
assert rate != null
assert rate.length()>0
log.info 'the rate is' +rate

//write code to trnasfer the value to the test case level
testRunner.testCase.setPropertyValue('newrate', rate)

//Read value from test case level from newrate
log.info testRunner.testCase.getPropertyValue('newrate')
